INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `doctor_discrible`, `doctor_award`) VALUES ('Bác sĩ', 'Yi Hyeon Gyu', 'Ung bướu-Xạ trị', 'Đơn nguyên Huyết học và Trị liệu tế bào', 'Phó Giáo sư, Tiến sĩ, Bác sĩ Yi Hyeon Gyu là giảng viên khoa Huyết học và Ung bướu - Trường Đại học Y Inha, miền Nam Hàn Quốc. Bác sĩ đã có nhiều năm kinh nghiệm trong lĩnh vực Huyết học, truyền Tế bào gốc tạo máu và điều trị Ung bướu. Hiện bác sĩ là Trưởng đơn nguyên Huyết học và Trị liệu tế bào, nguyên là Trưởng đơn nguyên Nội trú Ung bướu của Bệnh viện Đa khoa Quốc tế Vinmec', 'Travel award, ICBMT 2018');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `doctor_discrible`) VALUES ('Bác sĩ', 'Martina Pellegrino', 'Sản khoa', 'Trung tâm Sức khỏe Phụ nữ - Bệnh viện Đa khoa Quốc tế Vinmec Times City', 'Bác sĩ Martina Pellegrino có kinh nghiệm 03 năm làm việc tại Khoa Sản Phụ - Bệnh viện Đại Học Geneva và Bệnh viện Sion – Thụy Sỹ; 03 năm kinh nghiệm làm việc tại Bệnh viện Đại học King – London – Anh Quốc. Sau đó bác sĩ có thời gian làm việc tại bệnh viện Từ Dũ, TP.HCM với 2500 ca chọc ối, 250 ca sinh thiết gai rau, 30 ca giảm ối/ truyền ối, 20 ca lấy máu thai nhi xét nghiệm/ truyền máu thai nhi)');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Kharitonchyk Aksana', 'Sản khoa', 'Trung tâm Sức khỏe Phụ nữ - Bệnh viện Đa khoa Quốc tế Vinmec Times City', 'Bác sĩ tại khoa sản phụ khoa- Bệnh viện phụ sản tại thành phố MinskBác sĩ tại Trung tâm Sức khỏe Phụ nữ - Bệnh viện Đa khoa quốc tế Vinmec Times City', 'Bác sĩ Kharitonchyk Aksana đã có 20 năm kinh nghiệm trong lĩnh vực Sản phụ khoa đồng thời bác sĩ cũng có nhiều kinh nghiệm trong tư vấn về y khoa nữ giới');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Philippe Macaire', 'Gây mê - điều trị đau', 'Khoa Gây mê giảm đau - Bệnh viện Đa khoa Quốc tế Vinmec Times City', 'Tại Hà Nội:Trưởng khoa Gây mê giảm đau bệnh viện Đa khoa Quốc tế Vinmec Times CityGiáo sư Dự án Đại học Y VinUni – Khối Khoa học sức khỏeTại Dubai:', '');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'EL Hage Sleiman', 'Tiêu hóa', 'Khoa Ngoại tổng hợp - Bệnh viện Đa khoa Quốc tế Vinmec Nha Trang', 'Khoa Ngoại tổng hợp - Bệnh viện Đa khoa Quốc tế Vinmec Nha Trang', 'Tiến sĩ. Bác sĩ Farouk Sleiman có nhiều năm kinh nghiệm trong chuyên ngành Ngoại tiêu hóa, thực hiện thành công hơn 10.000 ca phẫu thuật nội soi ổ bụng (giảm béo, tổng quát và phụ khoa).');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Mykhailo Dudik', 'Sản khoa', 'Khoa Sản phụ khoa - Bệnh viện Đa khoa Quốc tế Vinmec Nha Trang', 'Từ tháng 01/2017: Chuyên gia tư vấn Sản phụ khoa, Bệnh viện Đa khoa Quốc tế Vinmec Nha Trang', 'Với hơn 8 năm kinh nghiệm, bác sĩ Mykhailo Dudik đã làm việc, giữ vị trí quan trọng tại các bệnh viện ở nhiều quốc gia như: Đại học Y quốc gia Ivano- Frankivsk , bệnh viện Sonja Kill Memorial, Campuchia. Bác sĩ hiện đang làm việc tại Khoa Sản phụ khoa, Bệnh viện đa khoa quốc tế Vinmec Nha Trang từ năm 2017.');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Moreyra Francisco Carlos', 'Khám sức khỏe tổng quát', 'Khoa Khám bệnh & Nội khoa - Bệnh viện Đa khoa Quốc tế Vinmec Nha Trang', 'Bác sĩ Khoa Khám bệnh và Nội khoa Bệnh viện Đa khoa Quốc tế Vinmec Nha Trang', 'Bác sĩ Carlos đã có 23 năm kinh nghiệm điều trị các bệnh lý về tim mạch như động mạch vành, đánh giá và điều trị tăng huyết áp, rối loạn nhịp tim, các bệnh lý tim mạch bẩm sinh ở trẻ nhỏ, các bệnh lý cấp cứu tim mạch như hội chứng mạch vành cấp, cấp cứu tăng huyết áp, hội chứng ngất/ bất tỉnh. Hiện bác sĩ đang là Bác sĩ Nội tim mạch -  Khoa Khám bệnh và Nội khoa Bệnh viện Đa khoa Quốc tế Vinmec Nha Trang');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Reinel Martin Alvarez Plasencia', 'Tim mạch', 'Với hơn 20 năm làm việc tại các bệnh viện, Viện tim của Cuba, Venezuela,.. Thạc sĩ. Bác sĩ Reinel Martin Alvarez Plasencia có kinh nghiệm & thế mạnh trong siêu âm tim & điều trị các bệnh lý tim mạch.  ', 'Với hơn 20 năm làm việc tại các bệnh viện, Viện tim của Cuba, Venezuela,.. Thạc sĩ. Bác sĩ Reinel Martin Alvarez Plasencia có kinh nghiệm & thế mạnh trong siêu âm tim & điều trị các bệnh lý tim mạch.  ', 'Với hơn 20 năm làm việc tại các bệnh viện, Viện tim của Cuba, Venezuela,.. Thạc sĩ. Bác sĩ Reinel Martin Alvarez Plasencia có kinh nghiệm & thế mạnh trong siêu âm tim & điều trị các bệnh lý tim mạch.  ');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Bruno Schaub', 'Sản khoa', 'Trung tâm Sức khỏe Phụ nữ - Bệnh viện Đa khoa Quốc tế Vinmec Times City', 'Nguyên là Trưởng khoa Chẩn đoán trước sinh và y học thai nhi – Bệnh viện Đại học MartiniqueGiám đốc trung tâm Chẩn đoán trước sinh Martinique của Bệnh viện Đại học MartiniqueTrưởng khoa Sản Phụ Khoa và Nhi Khoa của Bệnh viện Đại học MartiniqueThành viên của Trường Đại học Siêu âm thai Quốc gia PhápThành viên Câu lạc bộ Francophone về Y học thai nhiGiám đốc trung tâm đăng ký các ca bệnh lý ở  trẻ em tại French West Indies thuộc Học viện Giám sát sức khỏe quốc gia Pháp', 'Bác sĩ Bruno Schaub – chuyên gia hàng đầu về siêu âm và bệnh lý thai nhi tại Pháp, đã có 30 năm kinh nghiệm trong lĩnh vực sản phụ khoa và là Chuyên gia giảng dạy về lĩnh vực này tại nhiều nước thuộc cộng đồng chung Châu Âu và các nước Châu Á.');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Jean Clement Sage', 'Hỗ trợ sinh sản', 'Trung tâm Sức khỏe Phụ nữ - Bệnh viện Đa khoa Quốc tế Vinmec Times CityTrung tâm Hỗ trợ sinh sản - Bệnh viện Đa khoa Quốc tế Vinmec Times City', 'Bác sĩ Trung tâm hỗ trợ sinh sản Bệnh viện Đa khoa Quốc tế Vinmec Times City', 'Bác sĩ Jean Clément Sage là một trong những chuyên gia hàng đầu về thụ tinh ống nghiệm tại Pháp. Ông là người đồng sáng lập và là thành viên điều hành của rất nhiều tổ chức như: Trung tâm PMA Grenoble Belledonne, Hiệp hội y học sinh sản và Cơ quan Thụ tinh ống nghiệm Pháp. Tiến sĩ hiện đang là Chuyên gia hỗ trợ sinh sản tại Trung tâm hỗ trợ sinh sản Bệnh viện Đa khoa Quốc tế Vinmec Times City');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`, `doctor_award`) VALUES ('Bác sĩ', 'Võ Thành Nhân', 'Tim mạch', 'Trung tâm Tim mạch - Bệnh viện Đa khoa Quốc tế Vinmec Central Park', 'Trung tâm Tim mạch - Bệnh viện Đa khoa Quốc tế Vinmec Central Park', 'Giáo sư Tiến sĩ Võ Thành Nhân từng có nhiều năm kinh nghiệm giảng dạy tại Đại học Y Dược TP HCM và công tác giảng dạy, điều trị tại bệnh viện Chợ Rẫy. Giáo sư, tiến sĩ, bác sĩ Chuyên khoa II Võ Thành Nhân đã đạt được nhiều thành tựu lớn về nghiên cứu trong lĩnh vực tim mạch, là tác giả của hơn 73 bài báo chuyên ngành và tham gia biên soạn 9 sách giáo khoa, tham khảo và chuyên khảo.', 'Được phong tăng Thầy thuốc ưu tú năm 2010Được nhà nước phong tặng Thầy thuốc nhân dân năm 2015Được nhà nước phong tặng Huân chương lao động hạng Nhì vào tháng 2 năm 2018');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`, `doctor_award`) VALUES ('Bác sĩ', 'Đỗ Doãn Lợi', 'Tim mạch', 'Trung Tâm Tim Mạch - Bệnh viện Đa khoa quốc tế Vinmec Times City', '12/1983: Giảng viên Bộ môn Tim mạch Đại học Y Hà Nội, Bác sĩ Viện Tim mạch - Bệnh viện Bạch Mai', 'Giáo sư. Tiến sĩ. Đỗ Doãn Lợi đã có nhiều năm giảng dạy và là Phó Hiệu trưởng tại Đại học Y Hà Nội; từng giữ nhiều vị trí quan trọng tại các bệnh viện như: Phó Giám đốc Bệnh viện Bạch Mai; Viện trưởng Viện Tim mạch...', 'Bằng khen của Bộ Y tế  các năm: 2004, 2005, 2007, 2008, 2009, 2010, 2011, 2012, 2013, 2015');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`, `doctor_award`) VALUES ('Bác sĩ', 'Phạm Nhật An', 'Nhi', 'Trung tâm Nhi - Bệnh viện Đa khoa Quốc tế Vinmec Times City', 'Nguyên Phó hiệu trưởng trường Đại học Y Hà nội, Phó giám đốc trung tâm Y học gia đình, Trưởng Bộ môn Y học gia đình, Trưởng phòng đào tạo sau đại học', 'Gs.Ts. Bs. Phạm Nhật An đã có 40 năm kinh nghiệm làm việc trong ngành và nguyên là Phó giám đốc Bệnh viện Nhi Trung Ương, trưởng bộ môn Nhi – Đại học Y Hà Nội, Trưởng khoa truyền nhiễm tại Bệnh viện Nhi Trung ương, Trưởng phòng đào tạo sau đại học thuộc Đại học Y Hà Nội (1998-2002), trưởng Bộ môn Y học gia đình (2005-2012). Hiện nay, GS.TS An đang là Giám đốc Trung tâm Nhi kiêm trưởng khoa Nội trú Nhi Bệnh viện Đa khoa Quốc tế Vinmec Times City', 'Huy chương vì sự nghiệp giáo dục (2006)');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Phan Quỳnh Lan', 'Dược', 'Khoa Dược - Bệnh viện Đa khoa Quốc tế Vinmec Times City', 'Tháng 10/1995 - 6/1998: Công ty Dược Hà Nội (Hapharco).', 'Tiến sĩ Phan Quỳnh Lan là người đã có rất nhiều năm kinh nghiệm trong lĩnh vực Dược lâm sàng. Tiến sĩ Lan nguyên là Giảng viên bộ môn Dược lâm sàng tại Đại học Dược Hà Nội từ năm 1998 đến năm 2011. Hiện tại, Tiến sĩ Phan Quỳnh Lan là Phó Tổng Giám đốc Công ty cổ phần Bệnh viện Đa khoa Quốc tế Vinmec và là Trưởng khoa Dược tại Bệnh viện Đa khoa Quốc tế Vinmec Times City.');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Nguyễn Đức Hinh', 'Sản khoa', 'Trung tâm Sức khỏe Phụ nữ - Bệnh viện Đa khoa Quốc tế Vinmec Times City', '1987 - Đến nay: Giảng viên bộ môn Phụ Sản, trường Đại học Y Hà Nội;', 'PGS. TS. BS Nguyễn Đức Hinh là chuyên gia đầu ngành trong lĩnh vực sản phụ khoa trong nước với nhiều cống hiến cho sự phát triển của ngành. Trong đó, đóng góp nổi bật nhất là sự cố gắng đặt những viên gạch đầu tiên cho những kỹ thuật phẫu thuật mới trong điều trị bệnh sản phụ khoa như kỹ thuật mổ âm đạo, tạo hình âm đạo…');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`, `doctor_award`) VALUES ('Bác sĩ', 'Nguyễn Thị Tân Sinh', 'Sản khoa', 'Trung tâm Sức khỏe Phụ nữ - Bệnh viện Đa khoa Quốc tế Vinmec Times CityKhoa Phụ - Bệnh viện Đa khoa Quốc tế Vinmec Times City', 'Nguyên Phó Trưởng khoa Sản Phụ - Bệnh viện Bạch MaiBác sĩ phối hợp, khoa Phụ Sản Bệnh viện Việt Úc, Bệnh viện Việt PhápPhó Giám đốc Trung tâm Sức khỏe Phụ nữ kiêm Trưởng khoa Phụ,  Bệnh viện Đa khoa Quốc tế Vinmec Times City', 'Bác sĩ cao cấp Nguyễn Thị Tân Sinh nguyên là Phó Trưởng khoa Phụ Sản Bệnh viện Bạch Mai, Giảng viên kiêm nhiệm Bộ môn Phụ - Sản, Đại học Y Hà Nội', 'Huy chương Vì sự nghiệp chăm sóc sức khỏe Nhân dân');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`, `doctor_award`) VALUES ('Bác sĩ', 'Quách Thanh Dung', 'Ung bướu-Xạ trị', 'Huy chương Vì sự nghiệp chăm sóc sức khỏe Nhân dân', 'Từ 2011 - 2016: Bác sĩ Nội khoa Ung thư tại Trung tâm Ung bướu Bệnh viện 198Từ tháng 2/2018 - Đến nay: Bác sĩ Nội khoa ung thư tại Trung tâm Ung bướu xạ trị - Bệnh viện Đa khoa Quốc tế Vinmec Time City', 'Thạc sĩ. Bác sĩ. Quách Thanh Dung có kinh nghiệm trong khám, chẩn đoán và điều trị Ung thư vú- phụ khoa, Ung thư đường tiêu hóa,..', 'Năm 2013: Giải Nhì Hội thi kỹ thuật sáng tạo tuổi trẻ ngành Y tế Hà Nội');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`, `doctor_award`) VALUES ('Bác sĩ', 'Tôn Thất Trí Dũng', 'Nội thần kinh', 'Khoa Khám bệnh & Nội khoa - Bệnh viện Đa khoa Quốc tế Vinmec Đà Nẵng', 'Tham gia giảng dạy và hướng dẫn sinh viên Hướng dẫn nhiều thạc sĩ và bác sĩ chuyên khoa 2', 'Tiến sĩ. Bác sĩ Tôn Thất Trí Dũng đã có trên 20 năm kinh nghiệm trong chuyên ngành thần kinh đột quỵ với nhiều giải thưởng trong nghiên cứu khoa học. Riêng can thiệp mạch não, bác sĩ đã thực hiện 6 năm và mỗi năm chụp và can thiệp khoảng 480 - 500 ca', 'Có nhiều giải thưởng trong nghiên cứu khoa học như:');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Nguyễn Văn Quyết', 'Khám sức khỏe tổng quát', 'Khoa Khám bệnh - Bệnh viện Đa khoa Quốc tế Vinmec Times City', 'Trước khi về làm việc tại Vinmec, Thạc sĩ - Bác sĩ Nguyễn Văn Quyết đã công tác tại Bệnh viện Đa khoa Đức Giang - Hà Nội chuyên khoa Nội tim mạch.', 'Thạc sĩ. Bác sĩ Nguyễn Văn Quyết đã có 14 năm hoạt động trong nghề, với các kỹ năng đặc biệt như:  Khám bệnh đa khoa, khám điều trị các bệnh lý nội tim mạch, khám tư vấn trước tiêm chủng người lớn. Hiện bác sĩ  đang giữ chức vụ Trưởng khoa Khám bệnh Bệnh viện Đa khoa Quốc tế Vinmec Times City.');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`, `doctor_award`) VALUES ('Bác sĩ', 'Tô Minh Hương', 'Phụ khoa', 'Trung tâm Sức khỏe Phụ nữ - Bệnh viện Đa khoa Quốc tế Vinmec Times City', 'Nguyên là Phó giám đốc kiêm trưởng khoa Hỗ trợ sinh sản, Bệnh viện Phụ sản Hà NộiTrưởng Đơn nguyên Hỗ trợ sinh sản lâm sàng Bệnh viện Đa khoa Quốc tế Vinmec Times City.', 'Tiến sĩ Tô Minh Hương đã có hơn 30 năm kinh nghiệm làm việc trong lĩnh vực Hỗ trợ sinh sản; được đào tạo sau đại học tại Vương quốc Bỉ, Thái Lan và Đào tạo chuyên sâu về Thụ tinh ống nghiệm tại Mỹ và Bệnh viện Phụ sản Trung Ương.', 'Nhận nhiều bằng khen của Bộ Y tế, Sở Y tế và thành phố Hà Nội trong lĩnh vực Hỗ trợ sinh sản');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Ngô Văn Đoan', 'Chẩn đoán hình ảnh', 'Khoa Chẩn đoán hình ảnh và Y học hạt nhân - Bệnh viện Đa khoa Quốc tế Vinmec Times City', 'Từ 2003 – 6/2009: Bác sĩ khoa Chẩn đoán hình ảnh Bệnh viện Bưu Điện II Tp Hồ Chí Minh.', 'Bác sĩ Đoan đã có hơn 16 năm kinh nghiệm trong lĩnh vực chẩn đoán hình ảnh, đặc biệt trong lĩnh vực siêu âm, cắt lớp vi tính đa dãy, cộng hưởng từ. Hiện tại, bác sĩ đang làm việc tại Khoa Chẩn đoán hình ảnh - Bệnh viện Đa khoa Quốc tế Vinmec Times City');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Nguyễn Thanh Hưng', 'Nhi', 'Khoa Nhi - Sơ sinh - Bệnh viện Đa khoa Quốc tế Vinmec Nha Trang', 'Năm 2012 - 2016: Bác sĩ điều trị tại Khoa Nhi, Bệnh viện Đại Học Y dược Hoàng Anh Gia Lai', 'Thạc sĩ. Bác sĩ Nguyễn Thanh Hưng đã có nhiều năm làm việc trong chuyên ngành Nhi khoa tổng quát và sơ sinh. Bác sĩ có nhiều kinh nghiệm trong khám, điều trị các bệnh lý chuyên sâu về hô hấp nhi khoa. Hiện nay, là Trưởng khoa Nhi - Sơ sinh - Bệnh viện Đa khoa Quốc tế Vinmec Nha Trang (Khánh Hòa)');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Nguyễn Tất Bình', 'Gây mê - điều trị đau', 'Khoa Gây mê phẫu thuật - Bệnh viện Đa khoa Quốc tế Vinmec Central Park', 'Bác sĩ Gây mê hồi sức Khoa Gây mê Hồi sức Viện Tim Thành Phố Hồ Chí Minh từ 2001 - 2015', 'Thạc sĩ. Bác sĩ Chuyên khoa II Nguyễn Tất Bình hiện đang là Trưởng khoa Gây mê Phẫu Thuật Bệnh viện Đa khoa Quốc tế Vinmec Central Park. Với thời gian công tác gần 14 năm trước đây tại Viện Tim Thành Phố Hồ Chí Minh và 3 năm trong môi trường Bệnh viện Đa khoa Quốc tế Vinmec Central Park, Bác sĩ Nguyễn Tất Bình là một chuyên gia trong lĩnh vực gây mê hồi sức phẫu thuật Tim mạch và các phẫu thuật bệnh lý ngoại khoa khác.');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Phạm Thị Hạnh Phúc', 'Khám sức khỏe tổng quát', 'Khoa Khám bệnh & Nội khoa - Bệnh viện Đa khoa Quốc tế Vinmec Hải Phòng', 'Trưởng khoa truyền nhiễm bệnh viện hữu nghị Việt Tiệp', 'Bác sĩ Phạm Thị Hạnh Phúc đã có kinh nghiệm 31 năm trong lĩnh vực Chuyên khoa Truyền nhiễm và Hồi sức Cấp cứu. Bác sĩ Phúc từng tham dự các lớp tập huấn và hội nghị các bệnh truyền nhiễm, gan mật tại Mỹ, Pháp, Thái Lan... Hiện đang là Trưởng khoa Nội khoa – Khám bệnh Bệnh viện Đa khoa Quốc tế Vinmec Hải Phòng');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Vũ Văn Soát', 'Nhi', 'Khoa Nhi - Sơ sinh - Bệnh viện Đa khoa Quốc tế Vinmec Hạ Long', 'Trưởng khoa cấp cứu; Trưởng khoa Nhi; trưởng khoa Sơ sinh tại bệnh viện Việt Nam-Thụy Điển Uông BíTrưởng khoa Nhi - Sơ sinh Bệnh viện Đa khoa Quốc tế Vinmec Hạ Long ', 'Bác sĩ Vũ Văn Soát đã có trên 34 năm kinh nghiệm trong ngành Nhi khoa và Hồi sức cấp cứu. Với  thế mạnh trong thực hiện thành thạo các kỹ thuật hồi sức Nhi và sơ sinh, bác sĩ từng cấp cứu hồi sức nuôi dưỡng hơn 1000 ca trẻ đẻ non và hơn 1000 ca các bệnh ở trẻ sơ sinh như: vàng da sơ sinh nặng, suy hô hấp sơ sinh,…');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`, `doctor_award`) VALUES ('Bác sĩ', 'Nguyễn Thị Hoàn', 'Nội tiết', 'Trung tâm Nhi - Bệnh viện Đa khoa Quốc tế Vinmec Times City', '1978 - 2013: Công tác liên tục tại Bệnh Viện Nhi Trung Ương', 'Phó giáo sư. Tiến sĩ Nguyễn Thị Hoàn đã có 40 năm kinh nghiệm chuyên khoa nhi và là bác sĩ đầu ngành chuyên khoa nội tiết Nhi tại Việt Nam. Bác sĩ Hoàn là một trong những người đầu tiên của Việt Nam làm về chương trình sàng lọc sơ sinh bệnh suy giáp bẩm sinh và thiếu G6PD và đã được Tổng cục Dân số - Bộ Y tế đưa vào chương trình trọng điểm quốc gia và đang triển khai trên 51/63 tỉnh thành trong toàn quốc. Bên cạnh đó, bác sĩ Hoàn cũng là một trong những người đầu tiên làm về sàng lọc nguy cơ cao bệnh rối loạn chuyển hóa bẩm sinh ở Việt nam. ', 'Năm 2005: Bằng khen của Thủ tướng Chính phủ');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Trần Thị Linh Chi', 'Nhi', 'Khoa Nhi - Sơ sinh - Bệnh viện Đa khoa Quốc tế Vinmec Hải Phòng', 'Trưởng phòng Thăm dò chức năng, trưởng khoa Tim mạch lồng ngực bệnh viện Trẻ em Hải Phòng', 'Bác sĩ Linh Chi đã công tác trong chuyên ngành nhi hơn 15 năm, có thời gian dài làm trưởng phòng Thăm dò chức năng, trưởng khoa Tim mạch lồng ngực bệnh viện Trẻ em Hải Phòng. Hiện đang là Trưởng khoa Nhi - Sơ sinh Bệnh viện Đa khoa Quốc tế Vinmec Hải Phòng');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Trần Liên Anh', 'Sơ sinh', 'Khoa Sơ sinh - Bệnh viện Đa khoa Quốc tế Vinmec Times City', 'Bác sĩ Khoa Hồi sức sơ sinh tại Bệnh viện Nhi Trung ương', 'BSCK II Trần Liên Anh từng tham gia giảng dạy các bệnh lý chuyên ngành Sơ sinh, có quá trình làm việc lâu dài tại Khoa Hồi sức sơ sinh tại Bệnh viện Nhi Trung ương từ năm 1979 đến 2012 và hiện đang làm việc trên cương vị Trưởng khoa Sơ sinh, Bệnh viện đa khoa quốc tế Vinmec Times City từ năm 2012 đến nay');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Đoàn Trung Hiệp', 'Ung bướu-Xạ trị', 'Trung tâm Xạ trị - Bệnh viện Đa khoa Quốc tế Vinmec Times City', '7/2015-12/2017: Phó giám đốc trung tâm ung bướu Bệnh viện Đa khoa Quốc tế Vinmec Times City , kiêm Trưởng đơn nguyên xạ trị', 'Ths.Bs Đoàn Trung Hiệp đã có trên 10 năm kinh nghiệm Nội khoa ung thư, đặc biệt trong lĩnh vực xạ trị và hóa trị, ghép tế bào gốc tạo máu.');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Huỳnh Thoại Loan', 'Nhi, Nhi Nội tiết - Chuyển hoá di truyền, Nhi Thận - Tiết niệu', 'Phòng khám chuyên sâu về nội tiết Nhi - Bệnh viện Đa khoa Quốc tế Vinmec Central ParkKhoa Nhi - Sơ sinh, Bệnh viện Đa khoa Quốc tế Vinmec Central Park', 'Từ 1989 -1992: Bác sĩ nội trú nhi, Bệnh Viện Nhi Đồng 1, Thành phố Hồ Chí Minh', 'Phó giáo sư. Tiến sĩ. Huỳnh Thoại Loan là một trong những chuyên gia hàng đầu về Nhi - Sơ sinh tại Thành phố Hồ Chí Minh với gần 30 năm kinh nghiệm khám và điều trị các bệnh về nội tiết nhi, thận nhi khoa và các vấn đề Nhi - Sơ sinh khác');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Phạm Thị Thùy Nhung', 'Xét nghiệm, Huyết học', 'Khoa Xét nghiệm - Bệnh viện đa khoa Quốc tế Vinmec Hải Phòng', 'Bác sĩ từng công tác tại trung tâm huyết học - truyền máu - Bệnh viện Việt Tiệp Hải Phòng, Khoa huyết học - Bệnh viện phụ sản Hải Phòng', 'Thạc sĩ. Bác sĩ. Nhung đã có 17 năm kinh nghiệm trong lĩnh vực huyết học với các mảng truyền máu, tế bào, đông máu. Trước khi là Trưởng khoa Xét nghiệm Bệnh viện Đa khoa Quốc tế Vinmec Hải Phòng, bác sĩ từng công tác tại trung tâm huyết học - truyền máu - bệnh viện Việt Tiệp Hải Phòng, Khoa huyết học - Bệnh viện phụ sản Hải Phòng.');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Bùi Tiến Đạt', 'Hồi sức - Cấp cứu, Tim mạch', 'Khoa Hồi sức cấp cứu - Bệnh viện đa khoa Quốc tế Vinmec Hải Phòng', 'Tháng 03/2018: Công tác tại Bệnh viện đa khoa Quốc tế Vinmec Hải Phòng', 'Thạc sĩ. Bác sĩ. Bùi Tiến Đạt được đào tạo chuyên sâu về lĩnh vực tim mạch và hồi sức cấp cứu tim mạch tại trường Đại học Y Hà Nội và Bệnh viện Bạch Mai. Bác sĩ đã có hơn 12 năm kinh nghiệm trong lĩnh vực hồi sức cấp cứu – tim mạch và hiện đang là bác sĩ tại Khoa Hồi sức cấp cứu Bệnh viện Đa khoa Quốc tế Vinmec Hải Phòng');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`, `doctor_award`) VALUES ('Bác sĩ', 'Đoàn Thị Hồng Hạnh', 'Xét nghiệm vi sinh', 'Khoa Xét nghiệm - Bệnh viện Đa khoa Quốc tế Vinmec Hạ Long', 'Từ 12/1985 - 6/2016: Công tác tại bệnh viện Việt Nam - Thụy Điển Uông Bí. Nguyên Phó giám đốc bệnh viện, Giám đốc Trung tâm đào tạo & Chỉ đạo tuyến, Trưởng khoa Vi sinh của bệnh viện Việt Nam - Thụy Điển Uông Bí', 'Tiến sĩ. Bác sĩ. Đoàn Thị Hồng Hạnh có 30 năm kinh nghiệm trong lĩnh vực Vi sinh lâm sàng, tư vấn kháng thuốc của vi khuẩn gây bệnh. Là chuyên gia giảng dạy và đánh giá chất lượng các phòng xét nghiệm của Bộ Y tế, chuyên gia đánh giá kỹ thuật của Văn phòng công nhận chất lượng (BoA). Hiện bác sĩ Hạnh đang là Trưởng khoa Xét nghiệm tại Bệnh viện Đa khoa Quốc tế Vinmec Hạ Long', 'Được tặng thưởng danh hiệu Thầy thuốc Ưu tú');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`, `doctor_award`) VALUES ('Bác sĩ', 'Nguyễn Thái Trí', 'Hồi sức - Cấp cứu', 'Khoa Hồi sức Cấp cứu - Bệnh viện Đa khoa Quốc tế Vinmec Đà Nẵng', '03/1997- 05/2017: Công tác tại khoa HSCC Bệnh Viện C Đà Nẵng đảm nhiệm phó trưởng khoa HSCC 4 năm và trưởng khoa cấp cứu giai đoạn 2009-2017', 'Với hơn 20 năm trong nghề trong đó có 04 năm đảm nhiệm vị trí Phó khoa Hồi Sức Cấp Cứu và 8 năm làm Trưởng khoa Cấp cứu tại bệnh viện C Đà Nẵng đã mang lại nhiều kinh nghiệm cho bác sĩ Nguyễn Thái Trí trong việc xử trí các trường hợp bệnh nhân nguy kịch, hỗ trợ và phối hợp với các khoa trong bệnh viện điều trị những trường hợp bệnh nhân nặng và phức tạp.', 'Bằng khen của Bộ trưởng Bộ y tế năm 2016');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`, `doctor_award`) VALUES ('Bác sĩ', 'Lê Trọng Bình', 'Gây mê hồi sức', 'Khoa Ngoại tổng hợp - Bệnh viện Đa khoa Quốc tế Vinmec Đà Nẵng', 'Bác sĩ, Phó khoa, Trưởng khoa Gây mê hồi sức - Bệnh viện Đa khoa Đà Nẵng: Từ 1990 đến 1996 là bác sĩ điều trị tại khoa Gây mê hồi sức, từ 1997 đến 2012 là bác sĩ phó khoa Gây mê - Hồi sức, từ 2013 đến 2016 là trưởng khoa Gây mê - Hồi sức - Bệnh viện Đa khoa Đà Nẵng', 'BSCK I Lê Trọng Bình đã có trên 28 năm làm Bác sĩ, Phó khoa, Trưởng khoa Gây mê hồi sức – Bệnh viện Đa khoa Đà Nẵng, 8 năm làm Bác sĩ Nội nhi – Bệnh viện Duy Xuyên – Quảng Nam.', 'Chiến sĩ thi đua cấp cơ sở tại Bệnh viện Đà Nẵng trong nhiều năm liền từ 1997 đến 2016');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Thái Bằng', 'Sản khoa, Phụ khoa', 'Khoa Sản phụ khoa - Bệnh viện Đa khoa Quốc tế Vinmec Nha Trang', '06/2020 - Đến nay: Giám Đốc Bệnh/Giám đốc chuyên môn Viện Vinmec Nha Trang2017 - 06/2020: Trưởng khoa Sản phụ khoa - Bệnh viện Đa khoa Quốc tế Vinmec Nha Trang2015 - 2016: Bệnh viện Đa khoa Quốc tế Vinmec Times City2013 - 2015: Bệnh viện Phụ sản Trung ương2005 - 2012: Bệnh viện Đa khoa Hà Tĩnh, Bệnh viện Phụ sản An Thịnh', 'Bác sĩ Thái Bằng đã có gần 20 năm kinh nghiệm trong chuyên ngành sản phụ khoa, chẩn đoán trước sinh và hỗ trợ sinh sản. Từng tham gia khám chữa bệnh Sản phụ khoa tại Bệnh viện Phụ Sản Trung ương, Bệnh viện Phụ sản An Thịnh, Bệnh viện Đa khoa tỉnh Hà Tĩnh, Bệnh viện Đa khoa Quốc tế Vinmec Times City, Bệnh viện Đa khoa Quốc tế Vinmec Nha Trang.');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Trần Hữu Tuấn', 'Tai - Mũi - Họng', 'Khoa Liên Chuyên Khoa - Bệnh viện Đa khoa Quốc tế Vinmec Times City', 'Bác sĩ khoa Ung bướu - Bệnh viện Tai - Mũi - Họng Trung ương', 'Thạc sĩ. Bác sĩ. Trần Hữu Tuấn đã có nhiều năm kinh nghiệm trong việc điều trị các bệnh về Tai - Mũi - Họng và đã có 3 năm làm việc cũng như được đào tạo Thạc sĩ Y học, Thạc sĩ Bác sĩ Nội trú tại Cộng hòa Pháp. Hiện tại bác sĩ đang là Trưởng khoa Liên Chuyên Khoa hệ ngoại Bệnh viện Đa khoa Quốc tế Vinmec Times City');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Khổng Trọng Thắng', 'Hồi sức - Cấp cứu', 'Khoa Hồi sức cấp cứu - Bệnh viện Đa khoa Quốc tế Vinmec Central Park', 'Nguyên Bác sĩ tại khoa Hồi sức cấp cứu - Bệnh viện Nhân Dân 115', 'Thạc sĩ. Bác sĩ. Thắng đã có kinh nghiệm 19 năm trong làm việc và giảng dạy về Hồi sức cấp cứu. Thực hiện thành thạo hầu hết các kỹ thuật về Hồi sức cấp cứu, cũng như có nhiều kinh nghiệm về kỹ thuật mới (thở máy, lọc máu liên tục, gan nhân tạo MARS, TPE, theo dõi huyết động chuyên sâu). Ngoài ra, bác sĩ còn được đào tạo về chuyên khoa Thận học, Thận nhân tạo và đang nắm vị trí Trưởng Khoa Hồi Sức Cấp Cứu Bệnh viện Đa Khoa Quốc Tế Vinmec Central Park.');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Phạm Ngọc Hoa', 'Chẩn đoán hình ảnh', 'Khoa Chẩn đoán hình ảnh - Bệnh viện đa khoa Quốc tế Vinmec Phú Quốc', 'Trưởng khoa chẩn đoán hình ảnh Bệnh viện Đa khoa Quốc Tế Thái Hòa Đồng Tháp. Phụ trách khoa chẩn đoán hình ảnh các bệnh viện tư nhân và phòng khám tại Thành phố Hồ Chí Minh', 'Bác sĩ chuyên khoa I Phạm Ngọc Hoa nguyên là Trưởng khoa chẩn đoán hình ảnh Bệnh viện Đa khoa Quốc Tế Thái Hòa Đồng Tháp. Phụ trách khoa chẩn đoán hình ảnh các bệnh viện tư nhân và phòng khám tại Thành phố Hồ Chí Minh. Bác sĩ Hoa đã có hơn 12 năm kinh nghiệm làm việc trong lĩnh vực Chẩn đoán hình ảnh và hiện là Bác sĩ Chẩn đoán hình ảnh Bệnh viện Đa khoa Quốc tế Vinmec Phú Quốc');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`, `doctor_award`) VALUES ('Bác sĩ', 'Dương Thế Vinh', 'Phục hồi chức năng', 'Khoa phục hồi chức năng - Bệnh viện Đa khoa Quốc tế Vinmec Times City', 'Nguyên phó trưởng khoa Phục hồi chức năng tại trung tâm Xương khớp - Chấn thương chỉnh hình- Phục hồi chức năng -  Bệnh viện E Trung ương', 'Thạc sĩ. Bác sĩ Dương Thế Vinh nguyên là Phó trưởng khoa Phục hồi chức năng Trung tâm Xương khớp – Chấn thương chỉnh hình – Phục hồi chức năng Bệnh viện E Trung ương. Bên cạnh đó, Thạc sĩ. Bác sĩ Dương Thế Vinh còn có 7 năm làm việc tại Trung tâm Y học Lao động – Phục hồi chức năng Bộ Nông nghiệp – Phát triển Nông thôn. Hiện tại, Thạc sĩ. Bác sĩ Vinh là Trưởng khoa Phục hồi chức năng, Bệnh viện Đa khoa Quốc tế Vinmec Times City', '01 Bằng khen Bộ Y tế; 01 Bằng khen tỉnh Lào Cai, cho thành tích tham gia công tác 1816 của Bộ Y tế năm 2010');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Nguyễn Quang Thắng', 'Hồi sức - Cấp cứu', 'Khoa Hồi sức tích cực - Bệnh viện Đa khoa Quốc tế Vinmec Times City', '1991 – 2001: Khoa Hồi sức Cấp cứu A9 Bệnh viện Bạch Mai2001 – 2010: Khoa Cấp cứu A9 Bệnh viện Bạch Mai2010 – T12/2018: Trưởng khoa Hồi sức Cấp cứu Bệnh viện Quốc tế Vinmec Times CityHiện nay: Trưởng khoa Hồi Sức Tích Cực kiêm Trưởng Đơn nguyên Hồi sức Nội Bệnh viện Quốc tế Vinmec Times City', 'Thạc sĩ. Bác sĩ Nguyễn Quang Thắng  có bề dày kinh nghiệm trong lĩnh vực Hồi sức Cấp cứu (20 năm công tác tại khoa HSCC A9 Bệnh Viện Bạch Mai; 07 năm tại khoa Hồi sức cấp cứu Bệnh viện Vinmec Times City). Bác sĩ cũng là Thành viên chính thức hiệp hội Tim phổi máy thế giới ELSO (Extracorporeal Life Support Organization). Hiện đang là Trưởng khoa Hồi Sức Tích Cực kiêm Trưởng Đơn nguyên Hồi sức Nội Bệnh viện Vinmec Times City');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Nguyễn Việt Anh', 'Chẩn đoán hình ảnh', 'Khoa Chẩn đoán hình ảnh - Bệnh viện Đa khoa Quốc tế Vinmec Nha Trang', 'Năm 2013 - 2015: Phó trưởng khoa Chẩn Đoán Hình Ảnh Bệnh viện Đa khoa tỉnh Khánh Hòa', '​BSCK I Nguyễn Việt Anh đã có 10 năm kinh nghiệm trong lĩnh vực chẩn đoán hình ảnh chuyên sâu về các bệnh lý trên hình ảnh CT đa lát cắt, hình ảnh cộng hưởng từ của hệ thần kinh, cột sống, cơ xương khớp, mô mềm, các bệnh lý u, viêm trong ổ bụng và các bệnh lý tim, mạch máu. Bác sĩ Việt Anh nguyên là bác sĩ phó trưởng khoa chẩn đoán hình ảnh bệnh viện Đa khoa tỉnh Khánh Hòa và hiện đang là Trưởng khoa Chẩn Đoán Hình Ảnh Bệnh viện Đa khoa Quốc tế Vinmec Nha Trang');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`, `doctor_award`) VALUES ('Bác sĩ', 'Lê Hữu Đồng', 'Khám sức khỏe tổng quát, Nội Tim mạch, Nội tổng quát', 'Khoa Khám bệnh & Nội khoa - Bệnh viện Đa khoa Quốc tế Vinmec Nha Trang', 'Năm 2011 - 2016: Bác sĩ điều trị tại phòng khám đa khoa Quốc Tế Sài GònNăm 2007 - 2016: Bác sĩ điều trị khoa Tim mạch, bệnh viện Thống Nhất TP. HCMNăm 2009 - 2010: Bác sĩ điều trị tại bệnh viện Đại học Y Dược TP. HCMNăm 2008 - 2010: Bác sĩ điều trị tại phòng khám đa khoa Victoria Mỹ MỹTừ 2016 - Đến nay: Bác sĩ Khoa Khám bệnh và Nội khoa Bệnh viện Đa khoa Quốc tế Vinmec Nha Trang.', 'Bác sĩ Đồng có thế mạnh và kinh nghiệm trong khám và điều trị bệnh Tim mạch, chuyển hóa: suy tim, tăng huyết áp, đái tháo đường, bệnh mạch vành tim, bệnh van tim, bệnh cơ tim, rối loạn nhịp tim, bệnh tim bẩm sinh, bệnh lý mạch máu, bệnh lý tuyến giáp, bệnh lý tiêu hóa – gan mật…Hiện đang là Trưởng khoa Khám bệnh và Nội khoa Bệnh viện Đa khoa Quốc tế Vinmec Nha Trang', 'Năm 2007: Tốt nghiệp loại giỏi Bác sĩ đa khoa -  đại học Y Dược HuếNăm 2010: Đảng viên trẻ tiêu biểu Thành phố Hồ Chí MinhNăm 2014, năm 2015: Chiến sĩ thi đua cấp cơ sở - Bệnh viện Thống Nhất Hồ Chí Minh');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Lê Nguyễn Trí Dũng', 'Hồi sức - Cấp cứu', 'Khoa Hồi sức cấp cứu - Bệnh viện Đa khoa Quốc tế Vinmec Phú Quốc', 'Bệnh Viện Đa Khoa khu vực tỉnh An Giang, Bệnh Viện Nhật Tân – An Giang', 'Bác sĩ Lê Nguyễn Trí Dũng, tốt nghiệp đại học Y Dược Tp. HCM, đã có 20 năm kinh nghiệm tại các Bệnh Viện Đa Khoa tỉnh An Giang, Bệnh Viện Nhật Tân – An Giang, Bệnh Viện Hạnh Phúc – An Giang trước khi là Bác sĩ Hồi sức cấp cứu Bệnh viện Đa khoa Quốc tế Vinmec Phú Quốc');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Phan Phi Tuấn', 'Tiêu hóa, Ngoại Tiêu', 'Khoa Ngoại tổng hợp - Bệnh viện Đa khoa Quốc tế Vinmec Phú Quốc', 'Bác sĩ tại Bệnh viện Nhân Dân Gia Định', 'Bác sĩ Phan Phi Tuấn với hơn 16 năm kinh nghiệm học tập và làm việc về phẫu thuật tiêu hóa, tiết niệu, Bác sĩ CKII. Phan Phi Tuấn từng học tập, thực hành và công tác tại các Bệnh viện Nhân Dân Gia Định, Bệnh viện Từ Dũ, Bệnh viện Bình Dân, Bệnh viện Chợ Rẫy, Bệnh viện Đại Học Y Dược, Bệnh viện Đa Khoa An Phước. Hiện tại, Bác sĩ đang làm việc tại Khoa Ngoại tổng hợp Bệnh viện Đa khoa Quốc tế Vinmec Phú Quốc');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Phạm Xuân Hùng', 'Gây mê - điều trị đau', 'Khoa Ngoại Tổng hợp & Gây mê - Bệnh viện đa khoa Quốc tế Vinmec Hải Phòng', 'Từ năm 2002-2017: Khoa gây mê hồi sức Bệnh viện Việt Tiệp Hải Phòng, phó trưởng khoa từ năm 2014Từ năm 2011-2017: Trưởng khoa gây mê hồi sức Bệnh viện Đại học Y Hải PhòngTừ năm 2002-2017: Giảng viên trường Đại học Y Hải Phòng chuyên ngành Gây mê hồi sứcTừ tháng 12/2017 - nay: Công tác tại Bệnh viện Đa khoa Quốc tế Vinmec Hải Phòng', 'Tiến sĩ - Bác sĩ Phạm Xuân Hùng đã có 16 kinh nghiệm chuyên khoa gây mê hồi sức tại các bệnh viện tuyến đầu của Hải Phòng như bệnh viện Hữu nghị Việt Tiệp, bệnh viện Đại học y Hải Phòng. Hiện đang là Trưởng khoa Ngoại tổng hợp - Gây mê giảm đau Bệnh viện Đa khoa Quốc tế Vinmec Hải Phòng');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Lê Viết Cường', 'Hồi sức - Cấp cứu', 'Khoa Hồi sức - Cấp cứu - Bệnh viện Đa khoa Quốc tế Vinmec Nha Trang', 'Phó khoa Hồi sức tích cực và chống độc Bệnh viện Đa khoa tỉnh Khánh Hòa từ năm 2017', 'Bác sĩ Lê Viết Cường đã có hơn 12 năm kinh nghiệm trong khám và điều trị các bệnh lý nội khoa, cấp cứu và hồi sức cấp cứu. Ngoài ra, bác sĩ Cường còn có khả năng thực hiện các kỹ thuật thận nhân tạo, lọc màng bụng ở bệnh nhân có bệnh thận mạn giai đoạn cuối, lọc máu liên tục, thay huyết tương. Hiện đang là Trưởng khoa Hồi sức cấp cứu Bệnh viện Đa khoa Quốc tế Vinmec Nha Trang');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Khổng Tiến Đạt', 'Chẩn đoán hình ảnh', 'Khoa Chẩn đoán hình ảnh - Bệnh viện Đa khoa Quốc tế Vinmec Hạ Long', 'Năm 2003 - 2009: Khoa Chẩn đoán hình ảnh Bệnh viện Đa khoa tỉnh Điện Biên.', 'Với hơn 14 năm làm việc trong lĩnh vực chẩn đoán hình ảnh BSCK II Khổng Tiến Đạt hiện đang là Bác sĩ chẩn đoán hình ảnh Bệnh viện Đa khoa Quốc tế Vinmec Hạ Long');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Đoàn Xuân Sinh', 'Chẩn đoán hình ảnh', 'Khoa Chẩn đoán hình ảnh - Bệnh viện đa khoa Quốc tế Vinmec Hải Phòng', 'Từ tháng 10/2002 - 03/2006: Công tác tại Phòng khám đa khoa số 7 Lạch TrayTừ tháng 03/2006 - 02/2014: Công tác tại bệnh viện Trẻ em Hải PhòngTừ tháng 03/2014 - 05/2014: Công tác tại bệnh viện Đa khoa quốc tế Vinmec Times CityTừ tháng 05/2014 - 12/2017: Công tác tại Khoa Chẩn đoán hình ảnh, bệnh viện Đa khoa Quốc Tế Hải Phòng', 'Thạc sĩ. Bác sĩ Đoàn Xuân Sinh đã có 17 năm kinh nghiệm trong chuyên ngành chẩn đoán hình ảnh. Đặc biệt có rất nhiều kinh nghiệm trong chuyên ngành chẩn đoán hình ảnh nhi (siêu âm thóp, siêu âm nhi tổng quát chẩn đoán các bệnh lý bẩm sinh). Được đào tạo chuyên sâu về lĩnh vực cộng hưởng từ (cộng hưởng từ mạch não, mạch thận không tiêm thuốc đối quang, cộng hưởng từ đám');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Nguyễn Công Cảnh', 'Hồi sức - Cấp cứu, Nhi, Nhi Truyền nhiễm, Sơ sinh', 'Khoa Nhi - Sơ sinh - Bệnh viện Đa khoa Quốc tế Vinmec Đà Nẵng', 'Bác sĩ tại Khoa Hồi sức nhi Bệnh viện Đa khoa Đà Nẵng', 'Thạc sĩ. Bác sĩ Nguyễn Công Cảnh đã có gần 20 năm kinh nghiệm trong lĩnh vực nhi khoa, đặc biệt có rất nhiều kinh nghiệm trong lĩnh vực về Hô hấp, Tiêu hóa, Các bệnh truyền nhiễm trẻ em và Hồi sức cấp cứu nhi khoa nâng cao. Hiện tại, đang là Trưởng khoa Nhi - Sơ sinh - Bệnh viện Đa khoa Quốc tế Vinmec Đà Nẵng');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Đặng Văn Pháp', 'Sản khoa', 'Khoa Sản Phụ Khoa - Bệnh viện Đa khoa Quốc tế Vinmec Đà Nẵng', '06/2020 - Đến nay: Bác sĩ Sản phụ khoa, Khoa Sản phụ khoa, Bệnh viện Đa khoa Quốc tế Vinmec Đà Nẵng2016 - 05/2020: Trưởng khoa Sản Phụ khoa bệnh viện Đa khoa Quốc tế Vinmec Đà NẵngNăm 1999 – 2016: Phó trưởng khoa sản phụ sản Bệnh viện Đại học Y dược HuếNăm 1981 – 2016: Giảng viên trưởng bộ môn phụ sản trường Đại học Y dược HuếNăm 1981 – 1999: Bác sĩ điều trị Bệnh viện Trung ương Huế ( Khoa sản)', 'Với gần 40 làm nghề, Bác sĩ Đặng Văn Pháp là một trong những bác sĩ, chuyên gia gia giàu kinh nghiệm nhất của Vinmec Đà Nẵng. Có kinh nghiệm chuyên sâu và thế mạnh trong việc chẩn đoán, tư vấn cũng như chữa trị các bệnh lý Sản Phụ khoa như:');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Trần Như Tú', 'Chẩn đoán hình ảnh', 'Khoa Chẩn đoán hình ảnh - Bệnh viện Đa khoa Quốc tế Vinmec Đà Nẵng', 'Phó trưởng Khoa cận lâm sàng - Bệnh viện Hòa Vang', 'Tiến sĩ Trần Như Tú với kinh nghiệm trên 20 năm, trong đó 3 năm làm Phó trưởng Khoa cận lâm sàng - Bệnh viện Hòa Vang, 16 năm làm Phó khoa Chẩn đoán hình ảnh tại Bệnh viện Đà Nẵng, 4 năm làm Trưởng khoa Chẩn đoán hình ảnh tại bệnh viện Phụ sản - Nhi Đà Nẵng; Tiến sĩ Tú chẩn đoán chính xác, giảm tối thiểu sai sót dựa trên các hình ảnh thu được, sẵn sàng phục vụ bệnh nhân 24/24 trong các trường hợp khẩn cấp, cấp cứu.');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`, `doctor_award`) VALUES ('Bác sĩ', 'Ngô Văn Trung', 'Xét nghiệm, Giải phẫu bệnh', 'Khoa Xét nghiệm - Bệnh viện Đa khoa Quốc tế Vinmec Nha Trang', 'Năm 1981 - 1984: Cán bộ giảng dạy - Bộ môn giải phẫu bệnh- Trường đại học Y khoa Huế', 'Ths.Bs. Ngô Văn Trung đã có 30 năm kinh nghiệm trong lĩnh vực giảng dạy, nghiên cứu, chẩn đoán tế bào- mô bệnh học đặc biệt trong lĩnh vực chẩn đoán ung thư đường tiêu hóa, hô hấp và u mô mềm dưới da. Bác sĩ nguyên là trưởng khoa Giải Phẫu bệnh, Bệnh viện Trường Đại Học Y Dược Huế và Trưởng Bộ môn Giải Phẫu Bệnh –Y pháp, Trường Đại Học Y Dược Huế trước khi là Trưởng khoa Xét nghiệm, Bệnh viện Đa khoa Quốc tế Vinmec Nha Trang như hiện nay', 'Giải nhất Hội nghị lao động sáng tạo Trường Đại học Y Dược Huế lần thứ II năm 2006');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Nguyễn Quốc Tuấn', 'Nội Thận - Tiết niệu', 'Khoa Nội tổng hợp - Bệnh viện Đa khoa Quốc tế Vinmec Times City', 'Trưởng Đơn nguyên Nội Thận - Lọc Máu, khoa Nội tổng hợp, Bệnh viện Đa khoa Quốc tế Vinmec Times City', 'Thạc sĩ. Bác sĩ Nguyễn Quốc Tuấn có nhiều kinh nghiệm trong điều trị các bệnh lý về thận như bệnh cầu thận, ống thận, kẽ thận; điều trị bảo tồn suy thận; điều trị thay thế thận (lọc máu và ghép thận). Trước khi là Trưởng Đơn nguyên Thận - Lọc máu, Bệnh viện Đa khoa Quốc tế Vinmec Times City, bác sĩ nguyên là Phó Trưởng Khoa Thận lọc máu - Bệnh viện Đại học Y Hà Nội.');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Phạm Thị Huyền Trang', 'Răng - Hàm - Mặt', 'Khoa Liên Chuyên Khoa - Bệnh viện Đa khoa Quốc tế Vinmec Times City', 'Tham gia công tác giảng dạy tại Khoa Quốc tế, Đại học Quốc gia Hà Nội, chuyên ngành Nha khoa  phối hợp Đại học Nantes - Cộng hòa Pháp. .', 'Thạc sĩ. Bác sĩ. Phạm Thị Huyền Trang từng tham gia công tác giảng dạy tại trong và ngoài nước, nguyên là bác sĩ tham vấn Răng Hàm Mặt Bệnh viện Việt Pháp và hiện đang phụ trách Đơn nguyên Răng - Hàm - Mặt tại Khoa Liên chuyên khoa Bệnh viện Đa khoa Quốc tế Vinmec Times City');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Vũ Tuấn Việt', 'Gây mê - điều trị đau', 'Khoa Gây mê giảm đau - Bệnh viện Đa khoa Quốc tế Vinmec Times City', '2000 - 2003: Bác sĩ nội trú Gây mê Hồi sức( GMHS) tại Đại học Y Hà nội và Bệnh viện Việt Đức', 'Thạc sĩ. Bác sĩ. Vũ Tuấn Việt nguyên là Bác sĩ tại Khoa Gây mê hồi sức Bệnh viện Việt Đức – Hà Nội. Có kinh nghiệm làm việc trong các phòng mổ: Cấp cứu, Tim - lồng ngực, tiêu hóa, tiết niệu, chấn thương chỉnh hình, nhi, hữu trùng, thần kinh sọ não, nội soi tiêu hóa, nội soi tiết niệu, cột sống, phòng hồi sức ngoại khoa, hồi sức sau mổ tim…Cùng với hơn 15 năm kinh nghiệm trong ngành, hiện Bác sĩ đang là Phó trưởng Khoa Gây mê giảm đau Bệnh viện Đa khoa Quốc tế Vinmec Times City');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Nguyễn Ngọc Khánh', 'Tiêu hóa, Ngoại Tiêu hoá', 'Đơn nguyên Ngoại tiêu hóa tiết niệu phẫu thuật Robot & Ngoại Nhi - Bệnh viện Đa khoa Quốc tế Vinmec Times City, Khoa Ngoại tổng hợp - Bệnh viện Đa khoa Quốc tế Vinmec Times City', 'Từ 2000 - 2004: Bác sĩ phẫu thuật và nội soi tại phòng nội soi Bệnh viện Việt ĐứcTừ 2004 - 2013: Bác sĩ phẫu thuật, nguyên phó khoa phẫu thuật tiêu hóa-gan mật và hậu môn trực tràng bệnh viện Saint PaulTừ 2013 - Đến nay: Bác sĩ phẫu thuật Đơn nguyên Ngoại tiêu hóa tiết niệu phẫu thuật Robot & Ngoại Nhi - Bệnh viện Đa khoa Quốc tế Vinmec Times City', 'Thạc sĩ. Bác sĩ. Nguyễn Ngọc Khánh đã có kinh nghiệm trên 20 năm phẫu thuật ổ bụng trong lĩnh vực tiêu hóa-gan mật. Thực hiện các phẫu thuật chuyên sâu về ống tiêu hóa, gan mật tụy, hậu môn-trực tràng. Với các loại hình phẫu thuật mổ Robot, mổ nội soi đơn thuần hay 1 cổng và cả mổ mở. Bác sĩ là 1 trong 3 phẫu thuật viên Việt nam được ghi danh trong danh sách phẫu thuật Robot thế giới của chuyên trang davincisurgery.com');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Nguyễn Đắc Nghĩa', 'Phẫu Thuật chỉnh hình, Ngoại chấn thương chỉnh hình', 'Đơn nguyên Chấn thương - Chỉnh hình - Bệnh viện Đa khoa Quốc tế Vinmec Times City, Khoa Ngoại tổng hợp - Bệnh viện Đa khoa Quốc tế Vinmec Times City', '1981-1987: Bác sĩ quân y - Đoàn chuyên gia 478 Campuchia', 'Tiến sĩ. Bác sĩ Nguyễn Đắc Nghĩa tốt nghiệp Bác sĩ đa khoa tại Hungary năm 1978. Sau hơn 06 năm tham gia chiến trường Campuchia với cương vị Bác sĩ quân y, bác sĩ Nghĩa đã đăng ký theo học các khóa chuyên khoa Ngoại và trở thành Bác sĩ chuyên Chấn thương Chỉnh hình từ năm 1989.  ');
