INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Hà Thị Thu Hiên', 'Giải phẫu bệnh', 'Khoa Xét nghiệm - Bệnh viện Đa khoa Quốc tế Vinmec Times City', 'Năm 2002-2012: Khoa Giải phẫu bệnh- Bệnh viện Việt Đức', 'Thạc sĩ. Bác sĩ  Hà Thị Thu Hiên đã có hơn 17 năm kinh nghiệm trong lĩnh vực giải phẫu bệnh. Bác sĩ Hiên tốt nghiệp thạc sĩ  chuyên ngành giải phẫu bệnh tại Đại học Y Hà Nội sau đó có thời gian công tác dài tại Khoa Giải phẫu bệnh - Bệnh viện Việt Đức trước khi là bác sĩ Giải phẫu bệnh khoa Xét nghiệm, Bệnh viện Đa khoa Quốc tế Vinmec Times City');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Nguyễn Viết Nhân', 'Di truyền y học', 'Trung tâm Sức khỏe Phụ nữ - Bệnh viện Đa khoa Quốc tế Vinmec Times City, Khoa Y học', '2018 - Đến nay: Chuyên gia tư vấn di truyền tại Bệnh viện ĐKQT Vinmec Times city', 'PGS. TS . BS. Nguyễn Viết Nhân đã có gần 40 năm kinh nghiệm trong lĩnh vực Di truyền Y học. Trong quá trình công tác tại Đại Học Y Dược Huế và Bệnh viện trường Đại Học Y Dược Huế, bác sĩ đã đảm nhiệm các vị trí quan trọng như: Trưởng Bộ môn Di truyền Y học,  Trưởng Đơn vị Xét nghiệm trung tâm & Phó giám đốc Viện Y Sinh học, Phó Giám Đốc Phụ Trách Trung tâm Sàng Lọc - Chẩn Đoán Trước sinh và Sơ sinh chịu trách nhiệm cho 11 tỉnh miền Trung  theo quyết định của Bộ Y tế. Bác sĩ đã tham gia giảng dạy đại học, sau đại học trong lĩnh vực di truyền y học, sàng lọc chẩn đoán trước sinh và sơ sinh,  tư vấn di truyền cho các trường hợp dị tật bẩm sinh. Tại bệnh viện Đa khoa Quốc tế Vinmec Times City bác sĩ hiện đang là Chuyên gia tư vấn di truyền.');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Lê Thị Phương Lan', 'Sản khoa, Hỗ trợ sinh sản', 'Trung tâm Hỗ trợ sinh sản - Bệnh viện Đa khoa Quốc tế Vinmec Times City, Khoa sản phụ khoa - Bệnh viện Đa khoa Quốc tế Vinmec Hạ Long,', 'Nguyên là Phó giám đốc Trung tâm Hỗ trợ sinh sản, Bệnh viện Phụ sản Trung Ương.', 'Thạc sĩ. Bác sĩ. Lê Thị Phương Lan đã có 30 năm kinh nghiệm làm việc trong lĩnh vực Hỗ trợ sinh sản. Bác sĩ Lan là Chủ nhiệm và tham gia 6 đề tài cấp Nhà nước, cấp bộ, cấp cơ sở cũng như tác giả và đồng tác giả của nhiều bài báo trong nước và Quốc tế. Hiện Bác sĩ đang là Trưởng Đơn nguyên Hỗ trợ sinh sản Phòng Lab Bệnh viện Đa khoa Quốc tế Vinmec Times City');
