INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Phạm Minh Kiêm', 'Sản khoa', 'Trung tâm Sức khỏe Phụ nữ - Bệnh viện Đa khoa Quốc tế Vinmec Times City, Khoa Sản - Bệnh viện Đa khoa Quốc tế Vinmec Times city', 'Năm 2005 – 2012: Khoa Chẩn đoán hình ảnh, Bệnh viện Hữu NghịBác sĩ siêu âm sản phụ khoa tại Bệnh viện Đa khoa Quốc tế Vinmec Times City', 'Bác sĩ Phạm Minh Kiêm hiện là bác sĩ siêu âm tại Trung tâm Sức khỏe Phụ nữ, Bệnh viện Đa khoa Quốc tế Vinmec Times City từ năm 2012. Bác sĩ Khiêm có thế mạnh về siêu âm hình thái thai nhi');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Hoàng Thị Thu Hương', 'Sản khoa', 'Trung tâm Sức khỏe Phụ nữ - Bệnh viện Đa khoa Quốc tế Vinmec Times City, Khoa Sản - Bệnh viện Đa khoa Quốc tế Vinmec Times city', 'Năm 2004 - 2014: Bác sĩ Chẩn đoán hình ảnh tại Bệnh viện Giao thông vận tải Trung ƯơngNăm 2014 - Đến nay: Bác sĩ Siêu âm Sản phụ khoa tại Bệnh Viện Đa khoa Quốc tế Vinmec Times city', 'Bác sĩ Hoàng Thị Thu Hương có trên 15 năm kinh nghiệm về siêu âm Sản phụ khoa và hình thái học thai nhi. Sau khi tốt nghiệp Đại học Y Thái Bình năm 2003, Bác sĩ Hương đã làm việc tại Bệnh viện Giao thông vận tải Trung ương. Năm 2012, Bác sĩ Hương đã tốt nghiệp Bác sĩ chuyên khoa I Chẩn đoán hình ảnh tại trường Đại học Y Hà Nội. ');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Nguyễn Quang Nam', 'Sản khoa', 'Trung tâm Sức khỏe Phụ nữ - Bệnh viện Đa khoa Quốc tế Vinmec Times City, Khoa Sản - Bệnh viện Đa khoa Quốc tế Vinmec Times city', 'Trước khi công tác và làm việc tại Trung tâm Sức khỏe Phụ nữ - Bệnh viện Đa khoa quốc tế Vinmec Times City, bác sĩ từng qua các lớp đào tạo về Chẩn đoán hình ảnh tại Bệnh viện Bạch Mai và bác sĩ chuyên khoa I chẩn đoán hình ảnh Đại học Y Hà Nội.', 'Bác sĩ Nguyễn Quang Nam có kinh nghiệm, chuyên môn cao về siêu âm sản phụ khoa như siêu âm hình thái thai nhi, siêu âm phụ khoa,...Chẩn đoán trước sinh va quản lý thai, sàng lọc, chẩn đoán trước sinh, các bệnh lý di truyền');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Lê Thị Hiếu', 'Sản khoa, Phụ khoa', 'Khoa Phụ - Bệnh viện Đa khoa Quốc tế Vinmec Times City, Trung tâm Sức khỏe Phụ nữ - Bệnh viện Đa khoa Quốc tế Vinmec Times City', 'Bác sĩ khoa Ngoại sản, Phó Giám đốc Trung tâm Kế hoạch hóa gia đình - Bệnh viện Giao thông vận tải', 'ThS.BS Lê Thị Hiếu đã có trên 33 năm kinh nghiệm với các thế mạnh trong khám điều trị bệnh lý sản phụ khoa đặc biệt trong phẫu thuật mở và nội soi phụ khoa. Trước khi công tác tại Bệnh viện Đa khoa Quốc tế Vinmec Times City, bác sĩ Hiếu đã từng đảm nhiệm chức vụ Trưởng khoa Phụ sản tại Bệnh viện Giao thông vận tải.');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Đinh Xuân Anh Tuấn', 'Nội Hô hấp', 'Đơn nguyên Hô hấp - Dị ứng Miễn dịch lâm sàng - Bệnh viện Đa khoa Quốc tế Vinmec Times City, Khoa Nội tổng hợp - Bệnh viện Đa khoa Quốc tế Vinmec Times City', 'Giám đốc Dự án Trung tâm Hô hấp Dị ứng Miễn dịch Lâm sàng kiêm Cố vấn cao cấp khối Nội, Đại diện tại Châu Âu của Chuỗi Bệnh viện Đa khoa Quốc tế Vinmec', 'Giáo Sư. Tiến sĩ Đinh Xuân Anh Tuấn là chuyên gia đầu ngành của ngành Hô hấp Pháp và Châu Âu; là Trưởng khoa Thăm dò chức năng Hô hấp tại Bệnh viện Cochin Paris, Chủ nhiệm Y khoa năm thứ nhất, Chủ nhiệm Bộ môn “Sinh lý - Dược lý - Độc chất học” của Viện trường Đại học Y Paris Descartes, đồng thời là Hiệu trưởng Viện Trường Đại học Y khoa Corse.');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Tô Thị Thanh Hương', 'Sản khoa', 'Trung tâm Sức khỏe Phụ nữ - Bệnh viện Đa khoa Quốc tế Vinmec Times City, Khoa Sản - Bệnh viện Đa khoa Quốc tế Vinmec Times city', 'Bác sĩ Tô Thị Thanh Hương hiện đang làm việc tại Trung tâm Sức khỏe Phụ nữ - Bệnh viện Đa khoa Quốc tế Vinmec Times City từ tháng 12 năm 2013', 'Bác sĩ Thanh Hương có nhiều kinh nghiệm trong khám, tư vấn, điều trị lâm sàng sản phụ khoa và thực hiện các biện pháp kế hoạch hóa gia đình');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`, `doctor_award`) VALUES ('Bác sĩ', 'Nguyễn Đăng Đại', 'Thẩm mỹ, Phẫu thuật thẩm mỹ', 'Khoa Ngoại tổng hợp - Bệnh viện Đa khoa Quốc tế Vinmec Times City, Trung tâm Thẩm mỹ Vinmec - View, Bệnh viện Đa khoa Quốc tế Vinmec Times City', '2009 - 2016: Bệnh viện C Thái Nguyên.', 'Bác sĩ Nguyễn Đăng Đại nguyên là học viên cao học khóa 20 chuyên ngành Phẫu thuật tạo hình - Đại học Y Hà Nội. Trực tiếp làm việc và nhận chuyển giao Kỹ thuật phẫu thuật tạo hình thẩm mỹ từ các chuyên gia trong nước và quốc tế đến từ Mỹ, Nhật, Hàn Quốc…( Gs Trần Thiết Sơn, Gs Mitsuru Seikido, Gs Choi Soon Woo, TS Hu Jung Woo, Gs Kim Baek Kyu…), đã có 9 năm kinh nghiệm trong chuyên ngành Chấn thương chỉnh hình, Phẫu thuật tạo hình thẩm mỹ và hiện đang là phẫu thuật viên tạo hình thẩm mỹ làm việc toàn thời gian tại Trung tâm Tạo hình Thẩm mỹ và Phẫu thuật Bàn tay Bệnh viện Đa khoa Quốc tế Vinmec Times City', '');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`, `doctor_award`) VALUES ('Bác sĩ', 'Vũ Đức Nhã', 'Răng - Hàm - Mặt, Chấn thương chỉnh hình hàm mặt, Nội nha, Thẩm mỹ, Phẫu thuật thẩm mỹ', 'Trung tâm Thẩm mỹ Vinmec - View, Bệnh viện Đa khoa Quốc tế Vinmec Times City', '2011 - 2014: Bệnh viện đa khoa Dr Miguel Enriquez, La Habana, Cuba;', 'Bác sĩ Vũ Đức Nhã được đào tạo bài bản tại nước ngoài và có gần 10 năm kinh nghiệm trong công tác chuyên môn. Anh có thế mạnh trong phẫu thuật tạo hình thẩm mỹ vùng mặt như điêu khắc khuôn mặt, hạ gò má, cắt góc hàm, chỉnh cằm, căng da mặt…; điều trị các bệnh lý, chấn thương, dị dạng vùng hàm mặt như chấn thương, khe hở môi vòm và di chứng, hàm hô, móm, lệch mặt…; ứng dụng laser và tế bào gốc trong điều trị, làm đẹp. Bác sĩ Vũ Đức Nhã rực tiếp làm việc và nhận chuyển giao Kỹ thuật phẫu thuật tạo hình thẩm mỹ từ Bệnh viện thẩm mỹ View Hàn Quốc - địa chỉ làm đẹp về chỉnh hình hàng đầu Hàn Quốc.', 'Dành được danh hiệu Vàng từ Đại học Y La Habana, năm 2014');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Phạm Hồng Thái', 'Thẩm mỹ', 'Trung tâm Thẩm mỹ Vinmec - View, Bệnh viện Đa khoa Quốc tế Vinmec Times City, Khoa Ngoại tổng hợp - Bệnh viện Đa khoa Quốc tế Vinmec Times City', '2005 - 2010: Bệnh viện đa khoa Thăng Long', 'Bác sĩ Phạm Hồng Thái đã có 15 năm kinh nghiệm trong lĩnh vực thẩm mỹ và làm đẹp, đặc biệt là các kỹ thuật chống');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Phạm Đức Thọ', 'Thẩm mỹ', 'Trung tâm Thẩm mỹ Vinmec - View, Bệnh viện Đa khoa Quốc tế Vinmec Times City', '2008 - 2018: Nghiên cứu viên tại Viện vệ sinh Dịch tễ Trung ương', 'Tiến sĩ, Bác sĩ Phạm Đức Thọ có hơn 10 năm làm việc tại Viện nghiên cứu, trao đổi, học tập và chia sẻ kinh nghiệm với các chuyên gia nổi tiếng về ứng dụng công nghệ hiện đại trong thẩm mỹ không xâm lấn');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`, `doctor_award`) VALUES ('Bác sĩ', 'Choi Soon Woo', 'Thẩm mỹ', 'Trung tâm Thẩm mỹ Vinmec - View, Bệnh viện Đa khoa Quốc tế Vinmec Times City', '1999 - 2001: Bác sĩ thực tập Khoa phẫu thuật thẩm mỹ Bệnh viện Đại học Quốc gia Seoul', 'Bác sĩ Choi Soon Woo là chuyên gia, bác sĩ chuyên ngành thẩm mỹ trường Đại học Quốc Gia - Seoul Hàn Quốc. Sau khi tốt nghiệp và làm việc ở ngay ngôi trường đã học một thời gian năm 2005 bác sĩ đã thành lập Bệnh viện Thẩm mỹ View được công nhận là bệnh viện phẫu thuật thẩm mỹ an toàn 16 năm không sự cố y tế. Năm 2019 nhận giải thưởng ghi nhận đóng góp to lớn cho sự phát triển ngành thẩm mỹ nâng ngực túi Bellagel. Bác sĩ phẫu thuật chính trong chương trình “Let me in” - Hàn Quốc các mùa 2,3,4,5 & được hãng Mentor, Motiva và J&J vinh danh là Nhân vật phẫu thuật nâng ngực nhiều nhất vùng Bắc Á.', 'Giải thưởng hạng mục cơ sở có lượng người tiêu thụ túi ngực Mentor, J&J nhiều nhất Bắc ÁGiải thưởng người có tầm ảnh hưởng trong việc sử dụng túi ngực Mentor, Bellagel và Motiva');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Yun Chang Woon', 'Thẩm mỹ', 'Trung tâm Thẩm mỹ Vinmec - View, Bệnh viện Đa khoa Quốc tế Vinmec Times City', '1999 - 2000: Bác sĩ thực tập Khoa phẫu thuật thẩm mỹ Bệnh viện Đại học Quốc gia Soon Chun Hyang', 'Bác sĩ Yun Chang Woon hiện là chuyên gia về các phẫu thuật thẩm mỹ xương hàm mặt, căng da, căng chỉ, mũi, mắt, tạo hình thành bụng tại Bệnh viện Thẩm mỹ View ở Seoul Hàn Quốc.');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Jeong Cheol', 'Thẩm mỹ', 'Trung tâm Thẩm mỹ Vinmec - View, Bệnh viện Đa khoa Quốc tế Vinmec Times City', 'Chuyên gia Phẫu thuật thẩm mỹ - Bệnh viện Thẩm mỹ View (Kangnam, Hàn Quốc)', 'Bác sĩ Jeong Cheol là chuyên gia của Bệnh viện Đại học Kyoengbuk và là thành viên chính thức của Hiệp hội Phẫu thuật Thẩm mỹ và Chỉnh hình, Hiệp hội Phẫu thuật thẩm mỹ xâm lấn tối thiểu Hàn Quốc');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Trần Văn Trọng', 'Thẩm mỹ, Phẫu thuật thẩm mỹ, Nhi', 'Khoa Ngoại tổng hợp - Bệnh viện Đa khoa Quốc tế Vinmec Đà Nẵng', '2003 - 2011 : Bác sĩ điều trị tại Bệnh viện Đa khoa Đà Nẵng, Bệnh viện Nhi Trung Ương Hà Nội.2011 - 2017 : Trưởng khoa Ngoại - Bệnh viện Phụ sản - Nhi Đà Nẵng.2017 - Đến nay : Bác sĩ chuyên khoa Ngoại tiêu hóa Khoa Ngoại tổng hợp - Bệnh viện Đa khoa Quốc tế Vinmec Đà Nẵng.', 'Bác sĩ Trọng có chứng chỉ hành nghề Ngoại khoa; phẫu thuật Tạo hình - Thẩm mỹ và có trên 10 năm kinh nghiệm trong Phẫu thuật Nhi và Phẫu thuật Tạo hình - Thẩm mỹ. Với lòng nhiệt huyết, niềm đam mê công việc, ham học hỏi và bàn tay khéo léo, bác sĩ Trọng đã sớm trở thành một trong những bác sĩ hàng đầu trong lĩnh vực Phẫu thuật Trẻ em đặc biệt là Phẫu thuật Nội soi và Phẫu thuật Tạo hình - Thẩm mỹ. Bác sĩ đã tạo nên hình ảnh Bác sĩ Ngoại nhi đầy thán phục không những trong nước mà còn với đồng nghiệp Quốc tế và luôn sẵn sàng chia sẻ kinh nghiệm cho đồng nghiệp.');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Nguyễn Công Hoàng', 'Phục hồi chức năng, Ngoại chấn thương chỉnh hình', 'Khoa Ngoại tổng hợp - Bệnh viện Đa khoa Quốc tế Vinmec Nha Trang', 'Năm 2010 - 2016: Trưởng bộ môn Ngoại trường Cao đẳng Y Tế Khánh Hòa; tham gia điều trị và phẫu thuật tại khoa Chấn Thương Chỉnh Hình bệnh viện tỉnh Khánh HòaNăm 2000 - 09/2010: Phó trưởng khoa chỉnh hình bệnh viện Điều Dưỡng và Phục Hồi chức năng Hòn ChồngBác sĩ chấn thương chỉnh hình tại  khoa Ngoại tổng hợp, Bệnh viện Đa khoa Quốc tế Vinmec Nha Trang', 'Bác sĩ Nguyễn Công Hoàng đã có rất nhiều năm kinh nghiệm trong lĩnh vực chấn thương chỉnh hình, đặc biệt bác sĩ có kinh nghiệm sâu về chỉnh hình nhi. Hiện nay, là Bác sĩ chấn thương chỉnh hình tại  khoa Ngoại tổng hợp, Bệnh viện Đa khoa Quốc tế Vinmec Nha Trang');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`, `doctor_award`) VALUES ('Bác sĩ', 'Phan Quang Đoàn', 'Hen - Dị ứng miễn dịch, Nội tổng quát', 'Khoa Nội tổng hợp - Bệnh viện Đa khoa Quốc tế Vinmec Times City', 'Khoa nội khoa chung bệnh viện Bạch MaiKhoa nội chung bệnh viện đại học Y Hà Nội.Khoa Nội Tổng hợp Bệnh viện Đa khoa Quốc tế Vinmec Times City', 'PGS. TS. Phan Quang Đoàn là chuyên gia có hơn 30 năm kinh nghiệm trong việc khám và điều trị các bệnh nội khoa chung, hen và các bệnh dị ứng tự miễn. Bác sĩ Đoàn từng có thời gian công tác dài tại bệnh viện Bạch Mai và bệnh viện Đại học Y Hà Nội trước khi là bác sĩ chuyên khoa Nội tổng hợp, dị ứng, miễn dịch tại Bệnh viện Đa khoa Quốc tế Vinmec Times City như hiện nay. ', 'Bằng khen của Thủ tướng Chính phủ (năm 2010).Bằng khen của Bộ Y tế năm 2008 và 2010.Danh hiệu thầy thuốc ưu tú năm 2010.Huy chương vì thế hệ trẻ năm 2004.');
INSERT INTO `doctor`.`Doctor_infor` (`work`, `doctor_name`, `major_name`, `hospital_name`, `work_experience`, `doctor_discrible`) VALUES ('Bác sĩ', 'Nguyễn Thị Minh Phương', 'Nội Thần kinh', 'Khoa Nội tổng hợp - Bệnh viện Đa khoa Quốc tế Vinmec Times City', 'Năm 2003-2011: Khoa Thần kinh - Bệnh viện Hữu Nghị', 'Thạc sĩ. Bác sĩ. Nguyễn Thị Minh Phương nguyên là bác sĩ tại Khoa Thần kinh - Bệnh viện Hữu Nghị, có kinh nghiệm nhiều năm trong Chẩn đoán và điều trị các bệnh thuộc chuyên ngành Thần kinh: Tai biến mạch máu não, Thoát vị đĩa đệm cột sống, Parkinson, Rối loạn giấc ngủ, Đau đầu...Hiện Bác sĩ Phương đang công tác tại Chuyên khoa Thần kinh, khoa Nội tổng hợp, Bệnh viện viện Đa khoa Quốc tế Vinmec Times City');
